import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getJob } from '../Services/JobService';
import CustomNavbar from '../CostumNavbar';
import CostumNavbar from "../CostumNavbar";

const JobDetails = () => {
    const { id } = useParams();
    const [job, setJob] = useState(null);

    useEffect(() => {
        getJob(id)
            .then(response => {
                setJob(response.data);
            })
            .catch(error => {
                console.error('Error fetching job details:', error);
            });
    }, [id]);

    if (!job) {
        return <div>Loading...</div>;
    }

    return (
    <>
        <CostumNavbar />
        <div>
            <h2>{job.title}</h2>
            <div>Pershkrimi: {job.description}</div>
            <div>Kerkesat: {job.requirements}</div>
            <div>Location: {job.location}</div>
            <div>Paga: {job.salary}</div>
            <div>Category: {job.jobType}</div>
            <div>Deadline: {job.deadline}</div>
        </div>
    </>
    );
}

export default JobDetails;
