import React, { useState } from 'react';
import { createJob } from './Services/JobService';
import { useNavigate } from 'react-router-dom';
import './PostJob.css'; // Import your CSS file for styling

function PostJob() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        requirements: '',
        location: '',
        salary: '',
        jobType: '',
        deadline: ''
    });

    const [formErrors, setFormErrors] = useState({
        title: false,
        description: false,
        requirements: false,
        location: false,
        salary: false,
        jobType: false,
        deadline: false
    });

    const [touchedFields, setTouchedFields] = useState({
        title: false,
        description: false,
        requirements: false,
        location: false,
        salary: false,
        jobType: false,
        deadline: false
    });

    const navigator = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        setTouchedFields({ ...touchedFields, [name]: true });
        setFormErrors({ ...formErrors, [name]: value.trim() === '' });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Check if any field is empty or untouched
        const isFormValid = Object.keys(formData).every(field => formData[field].trim() !== '' && touchedFields[field]);

        if (isFormValid) {
            try {
                const response = await createJob(formData);
                console.log(response);
                if (response.status === 201) {
                    console.log('Job posting created successfully');
                    setFormData({
                        title: '',
                        description: '',
                        requirements: '',
                        location: '',
                        salary: '',
                        jobType: '',
                        deadline: ''
                    });
                    setFormErrors({
                        title: false,
                        description: false,
                        requirements: false,
                        location: false,
                        salary: false,
                        jobType: false,
                        deadline: false
                    });
                    navigator('/success'); // Redirect to success page
                } else {
                    console.error('Failed to create job posting');
                }
            } catch (error) {
                console.log('Error:', error);
            }
        } else {
            console.alert('Please fill all the inputs');
        }
    };

    return (
        <div className="post-job-container">
            <h1 className="post-job-title">Post Job</h1>
            <form className="post-job-form" onSubmit={handleSubmit}>
                <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="Title" />
                {formErrors.title && <div className="error-message">Title is required</div>}

                <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description"></textarea>
                {formErrors.description && <div className="error-message">Description is required</div>}

                <input type="text" name="requirements" value={formData.requirements} onChange={handleChange} placeholder="Requirements" />
                {formErrors.requirements && <div className="error-message">Requirements are required</div>}

                <input type="text" name="location" value={formData.location} onChange={handleChange} placeholder="Location" />
                {formErrors.location && <div className="error-message">Location is required</div>}

                <input type="text" name="salary" value={formData.salary} onChange={handleChange} placeholder="Salary" />
                {formErrors.salary && <div className="error-message">Salary is required</div>}

                <input type="text" name="jobType" value={formData.jobType} onChange={handleChange} placeholder="Job Type" />
                {formErrors.jobType && <div className="error-message">Job Type is required</div>}

                <input type="date" name="deadline" value={formData.deadline} onChange={handleChange} placeholder="Deadline" />
                {formErrors.deadline && <div className="error-message">Deadline is required</div>}

                <button type="submit" className="post-job-button">Submit</button>
            </form>
        </div>
    );
}

export default PostJob;
