import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom'; // Import useParams

// Assuming your JobService functions are correctly implemented
import { deleteJob , listJobs } from '../Services/JobService';

function AllJobs() {
    const [jobs, setJobs] = useState([]);
    const { id } = useParams(); // Get the id parameter from the URL

    useEffect(() => {
        // Fetch all jobs when component mounts
        // this will change to fetching the jobs only of the company that is logged in
        fetchJobs();
    }, []);

    const fetchJobs = async () => {
        try {
            const response = await listJobs(); // Fetch all jobs
            setJobs(response.data); // Update state with fetched jobs
        } catch (error) {
            console.error('Error fetching jobs:', error);
        }
    };

    const handleDelete = async (jobId) => {
        try {
            await deleteJob(jobId);
            // Use functional update to ensure the latest state is used when filtering
            setJobs(prevJobs => prevJobs.filter(job => job.id !== jobId));
        } catch (error) {
            console.error('Error deleting job:', error);
        }
    };


    return (
        <div>
            <h1>All Jobs</h1>
            <ul>
                {jobs.map(job => (
                    <li key={job.id}>
                        <p>{job.title}</p>
                        <p>{job.description}</p>
                        <p>{job.location}</p>
                        <button onClick={() => handleDelete(job.id)}>Delete</button>
                        <Link to={`/edit-job/${job.id}`}>Edit</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AllJobs;
