import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import CostumNavbar from "./CostumNavbar";
import PostJob from "./PostJob";

function JobListing() {
    const [showModal, setShowModal] = useState(false);
    const locations = [
        'Deçan', 'Dragash', 'Drenas', 'Ferizaj', 'Fushë Kosovë', 'Gjakovë', 'Gjilan', 'Burim',
        'Kaçanik', 'Dardanë', 'Klinë', 'Lipjan', 'Malishevë', 'Mitrovicë', 'Kastriot', 'Pejë',
        'Besianë', 'Prishtinë', 'Prizren', 'Rahovec', 'Skenderaj', 'Suharekë', 'Shtërpcë', 'Shtime',
        'Viti', 'Vushtrri'
    ];

    const industriaOptions = [
        'Administratë', 'Arkitekturë', 'Art dhe Kulturë', 'Banka', 'Industria Automobilistike',
        'Retail dhe Distribuim', 'Ndërtimtari & Patundshmëri', 'Mbështetje e Konsumatorëve, Call Center',
        'Ekonomi, Financë, Kontabilitet', 'Edukim, Shkencë & Hulumtim', 'Punë të Përgjithshme', 'Burime Njerëzore',
        'Teknologji e Informacionit', 'Gazetari, Shtyp & Media', 'Ligj & Legjislacion', 'Menaxhment',
        'Marketing, Reklamim & PR', 'Inxhinieri', 'Shëndetësi, Medicinë', 'Industri Farmaceutike',
        'Prodhim', 'Shërbime Publike, Qeveritare', 'Siguri & Mbrojtje', 'Industri të Shërbimit',
        'Telekomunikim', 'Tekstil, Lëkurë, Industri Veshëmbathjeje', 'Menaxhment Ekzekutiv',
        'Gastronomi, Hoteleri, Turizëm', 'Përkthim, Interpretim', 'Transport, Logjistikë',
        'Industri e Përpunimit të Drurit'
    ];

    const handleApplyClick = () => {
        // Logic for applying to the job
        console.log('Applied to the job');
    };

    const handleWebpageLink = (url) => {
        window.open(url, '_blank');
    };

    return (
        <>
            <CostumNavbar />
            {/* Search Start */}
            <div className="container-fluid bg-primary py-5">
                <div className="container">
                    <div className="row g-3 justify-content-center align-items-center">
                        <div className="col-md-3">
                            <input type="text" className="form-control" placeholder="Keyword" />
                        </div>
                        <div className="col-md-3">
                            <select className="form-select">
                                <option value="">Industria</option>
                                {industriaOptions.map((option, index) => (
                                    <option key={index} value={option}>{option}</option>
                                ))}
                            </select>
                        </div>
                        <div className="col-md-3">
                            <select className="form-select">
                                <option selected>Location</option>
                                {locations.map((location, index) => (
                                    <option key={index} value={location}>{location}</option>
                                ))}
                            </select>
                        </div>
                        <div className="col-md-3">
                            <Button variant="dark" className="w-100" onClick={() => handleApplyClick()}>Search</Button>
                        </div>
                    </div>
                </div>
            </div>
            {/* Search End */}

            {/* Render PostJob component with locations and industria options props */}
            <PostJob locations={locations} industriaOptions={industriaOptions} />
        </>
    );
}

export default JobListing;
