import React, { useState } from 'react';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput
} from 'mdb-react-ui-kit';

function Signup() {
  // State variables for form fields and validation
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  // State variables to track whether each field has been touched
  const [nameTouched, setNameTouched] = useState(false);
  const [emailTouched, setEmailTouched] = useState(false);

  // Validation function
  const validateForm = () => {
    // Perform validation checks
    const isNameValid = name.trim() !== '';
    const isEmailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

    // Update touched state for each field
    setNameTouched(true);
    setEmailTouched(true);

    // Return true if all fields are valid, otherwise false
    return isNameValid && isEmailValid;
  };

  // Form submission handler
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate form before submission
    if (validateForm()) {
      // Form is valid, proceed with submission
      console.log('Form submitted successfully!');
      // Add your form submission logic here
    } else {
      // Form is invalid, show error messages or handle accordingly
      console.log('Form submission failed. Please check your inputs.');
    }
  };

  return (
    <div>
      <MDBContainer fluid className='p-4'>
        <MDBRow>
          <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>
            <h1 className="my-5 display-3 fw-bold ls-tight px-3">
              The best offer <br />
              <span className="text-primary">for your business</span>
            </h1>
          </MDBCol>
          <MDBCol md='6'>
            <MDBCard className='my-5'>
              <MDBCardBody className='p-5'>
                <form onSubmit={handleSubmit}>
                  <MDBInput
                    wrapperClass='mb-4'
                    placeholder='Name'
                    id='name'
                    type='text'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    onBlur={() => setNameTouched(true)}
                    invalid={nameTouched && !name.trim()}
                  />
                  {nameTouched && !name.trim() && <div className="text-danger">Name is required</div>}

                  <MDBInput
                    wrapperClass='mb-4'
                    placeholder='Email'
                    id='email'
                    type='email'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    onBlur={() => setEmailTouched(true)}
                    invalid={emailTouched && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)}
                  />
                  {emailTouched && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email) && <div className="text-danger">Invalid email</div>}

                  <MDBBtn className='w-100 mb-4' size='md' type="submit">Sign up</MDBBtn>
                </form>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </div>
  )
}

export default Signup;
