// JobDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getJob } from '../Services/JobService';
import { Button, Container, Card, Row, Col } from 'react-bootstrap';

const JobDetails = () => {
    const { id } = useParams();
    const [job, setJob] = useState(null);

    useEffect(() => {
        getJob(id)
            .then(response => {
                setJob(response.data);
            })
            .catch(error => {
                console.error('Error fetching job details:', error);
            });
    }, [id]);

    if (!job) {
        return <div>Loading...</div>;
    }

    return (
        <Container className="my-4">
            <h2 className="mb-4">Job Details</h2>
            <Card>
                <Card.Body>
                    <Row>
                        <Col>
                            <p><strong>Titulli:</strong> {job.title}</p>
                            <p><strong>Pershkrimi:</strong> {job.description}</p>
                            <p><strong>Kerkesat:</strong> {job.requirements}</p>
                            <p><strong>Location:</strong> {job.location}</p>
                            <p><strong>Paga:</strong> {job.salary}</p>
                            <p><strong>Category:</strong> {job.jobType}</p>
                            <p><strong>Deadline:</strong> {job.deadline}</p>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <Button variant="primary">Apply</Button>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>
        </Container>
    );
}

export default JobDetails;
