import React, { useState } from 'react';

function PostJob() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        requirements: '',
        location: '',
        salary: '',
        jobType: '',
        deadline: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/api/jobs', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });
            if (response.ok) {
                // Job posting successfully created
                console.log('Job posting created successfully');
                // Reset form
                setFormData({
                    title: '',
                    description: '',
                    requirements: '',
                    location: '',
                    salary: '',
                    jobType: '',
                    deadline: ''
                });
            } else {
                // Handle error
                console.error('Failed to create job posting');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <h1>Post a Job</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="Title" />
                <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description"></textarea>
                <input type="text" name="requirements" value={formData.requirements} onChange={handleChange} placeholder="Requirements" />
                <input type="text" name="location" value={formData.location} onChange={handleChange} placeholder="Location" />
                <input type="text" name="salary" value={formData.salary} onChange={handleChange} placeholder="Salary" />
                <input type="text" name="jobType" value={formData.jobType} onChange={handleChange} placeholder="Job Type" />
                <input type="date" name="deadline" value={formData.deadline} onChange={handleChange} placeholder="Deadline" />
                <button type="submit">Post Job</button>
            </form>
        </div>
    );
}

export default PostJob;
