import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getJob, updateJob } from '../Services/JobService';
import CostumNavbar from "../CostumNavbar";
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBBtn } from 'mdb-react-ui-kit';
import './EditJob.css'; // Import CSS file for custom styling

const EditJob = () => {
    const { id } = useParams();
    const navigator = useNavigate();

    const [formData, setFormData] = useState({
        title: '',
        description: '',
        requirements: '',
        location: '',
        salary: '',
        jobType: '',
        deadline: ''
    });

    useEffect(() => {
        if (id) {
            getJob(id)
                .then(response => {
                    const { title, description, requirements, location, salary, jobType, deadline } = response.data;
                    setFormData({ title, description, requirements, location, salary, jobType, deadline });
                })
                .catch(error => {
                    console.error('Error fetching job details:', error);
                });
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateJob(id, formData);
            navigator(`/job/${id}`);
        } catch (error) {
            console.error('Error updating job:', error);
        }
    };

    return (
        <>
            <CostumNavbar />
            <MDBContainer className="edit-job-container py-5">
                <MDBRow>
                    <MDBCol md="8" className="mx-auto">
                        <MDBCard>
                            <MDBCardBody>
                                <h2 className="text-center mb-4">Edit Job Posting</h2>
                                <form onSubmit={handleSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="title">Title</label>
                                        <MDBInput
                                            type="text"
                                            id="title"
                                            name="title"
                                            value={formData.title}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="description">Description</label>
                                        <MDBInput
                                            type="textarea"
                                            id="description"
                                            name="description"
                                            value={formData.description}
                                            onChange={handleChange}
                                            rows="6"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="requirements">Requirements</label>
                                        <MDBInput
                                            type="textarea"
                                            id="requirements"
                                            name="requirements"
                                            value={formData.requirements}
                                            onChange={handleChange}
                                            rows="6"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="location">Location</label>
                                        <MDBInput
                                            type="text"
                                            id="location"
                                            name="location"
                                            value={formData.location}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="salary">Salary</label>
                                        <MDBInput
                                            type="text"
                                            id="salary"
                                            name="salary"
                                            value={formData.salary}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="jobType">Job Type</label>
                                        <MDBInput
                                            type="text"
                                            id="jobType"
                                            name="jobType"
                                            value={formData.jobType}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="deadline">Deadline</label>
                                        <MDBInput
                                            type="date"
                                            id="deadline"
                                            name="deadline"
                                            value={formData.deadline}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="text-center">
                                        <MDBBtn type="submit">Save Changes</MDBBtn>
                                    </div>
                                </form>
                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        </>
    );
}

export default EditJob;
