import React, { useState } from 'react';
import { createJob } from './Services/JobService';
import { useNavigate } from 'react-router-dom';

function PostJob() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        requirements: '',
        location: '',
        salary: '',
        jobType: '',
        deadline: ''
    });

    const [formErrors, setFormErrors] = useState({
        title: false,
        description: false,
        requirements: false,
        location: false,
        salary: false,
        jobType: false,
        deadline: false
    });

    const [touchedFields, setTouchedFields] = useState({
        title: false,
        description: false,
        requirements: false,
        location: false,
        salary: false,
        jobType: false,
        deadline: false
    });

    const navigator = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        setTouchedFields({ ...touchedFields, [name]: true });
        setFormErrors({ ...formErrors, [name]: value.trim() === '' });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Check if any field is empty or untouched
        const isFormValid = Object.keys(formData).every(field => formData[field].trim() !== '' && touchedFields[field]);

        if (isFormValid) {
            try {
                const response = await createJob(formData);
                console.log(response);
                if (response.status === 201) {
                    console.log('Job posting created successfully');
                    setFormData({
                        title: '',
                        description: '',
                        requirements: '',
                        location: '',
                        salary: '',
                        jobType: '',
                        deadline: ''
                    });
                    setFormErrors({
                        title: false,
                        description: false,
                        requirements: false,
                        location: false,
                        salary: false,
                        jobType: false,
                        deadline: false
                    });
                    navigator('/success'); // Redirect to success page
                } else {
                    console.error('Failed to create job posting');
                }
            } catch (error) {
                console.log('Error:', error);
            }
        } else {
            console.log('Form contains errors');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" value={formData.title} onChange={handleChange} />
                {formErrors.title && <div>Title is required</div>}

                <textarea name="description" value={formData.description} onChange={handleChange}></textarea>
                {formErrors.description && <div>Description is required</div>}

                <input type="text" name="requirements" value={formData.requirements} onChange={handleChange} />
                {formErrors.requirements && <div>Requirements are required</div>}

                <input type="text" name="location" value={formData.location} onChange={handleChange} />
                {formErrors.location && <div>Location is required</div>}

                <input type="text" name="salary" value={formData.salary} onChange={handleChange} />
                {formErrors.salary && <div>Salary is required</div>}

                <input type="text" name="jobType" value={formData.jobType} onChange={handleChange} />
                {formErrors.jobType && <div>Job Type is required</div>}

                <input type="date" name="deadline" value={formData.deadline} onChange={handleChange} />
                {formErrors.deadline && <div>Deadline is required</div>}

                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default PostJob;
