import React, { useEffect, useState } from 'react';
import { listJobs } from '../Services/JobService';

function EditJob({ match }) {
    const jobId = match.params.id; // Get job ID from URL parameter
    const [jobData, setJobData] = useState({
        title: '',
        description: '',
        requirements: '',
        location: '',
        salary: '',
        jobType: '',
        deadline: ''
    });

    useEffect(() => {
        // Fetch job details using jobId
        fetchJobs(jobId).then(data => setJobData(data));
    }, [jobId]);

    const fetchJobs = async () => {
        try {
            const response = await listJobs();
            setJobs(response.data);
        } catch (error) {
            console.error('Error fetching jobs:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setJobData({ ...jobData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Update job details using jobId and jobData
        await updateJob(jobId, jobData);
        history.push('/alljobs');
    };

    return (
        <div>
            <h1>Edit Job</h1>
            <form onSubmit={handleSubmit}>
                {/* Input fields to edit job details */}
                <input type="text" name="title" value={jobData.title} onChange={handleChange} />
                <textarea name="description" value={jobData.description} onChange={handleChange}></textarea>
                {/* Other input fields */}
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default EditJob;
