import React, { useEffect, useState } from 'react';
import { Button, Card } from 'react-bootstrap';
import CostumNavbar from './CostumNavbar';
import { listJobs } from './Services/JobService';

function JobListing() {
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        fetchJobs();
    }, []);

    const fetchJobs = async () => {
        try {
            const response = await listJobs();
            setJobs(response.data);
        } catch (error) {
            console.error('Error fetching jobs:', error);
        }
    };

    const handleApplyClick = () => {
        console.log('Apply clicked'); // Placeholder, replace with actual logic
    };

    const handleWebpageLink = (url) => {
        window.open(url, '_blank');
    };

    return (
        <>
            <CostumNavbar />
            {/* Search Start */}
            <div className="container-fluid bg-primary py-5">
                <div className="container">
                    <div className="row g-3 justify-content-center align-items-center">
                        <div className="col-md-3">
                            <input type="text" className="form-control" placeholder="Keyword" />
                        </div>
                        {/* Other search inputs */}
                        <div className="col-md-3">
                            <Button variant="dark" className="w-100" onClick={() => handleApplyClick()}>Search</Button>
                        </div>
                    </div>
                </div>
            </div>
            {/* Search End */}
            {/* Job List Start */}
            <div className="container mt-5">
                <h2>List of Jobs:</h2>
                <div className="row">
                    {jobs.map(job => (
                        <div key={job.id} className="col-md-4 mb-4">
                            <Card>
                                <Card.Body>
                                    <Card.Title>{job.title}</Card.Title>
                                    <Card.Text>{job.description}</Card.Text>
                                    <Button variant="primary" onClick={handleApplyClick}>Apply</Button>
                                    <Button variant="link" onClick={() => handleWebpageLink(job.url)}>More Info</Button>
                                </Card.Body>
                            </Card>
                        </div>
                    ))}
                </div>
            </div>
            {/* Job List End */}
        </>
    );
}

export default JobListing;
